mapper.select:
select used_no as usedNo, member_id as memberId, used_type as usedType, used_title as usedTitle, used_content as usedContent, used_date as usedDate, used_status as usedStatus, used_info as usedInfo, used_pw as usedPw, read_count as readCount from (select ROWNUM as rnum, u.* from (select * from used_board order by used_no desc)u
where used_status=#{usedStatus}
) where rnum between #{start} and #{end}
